var nodex = require('./libs');

nodex.config = function(options) {
	if (!options)
		return;
	if (options.aescfg) {
		nodex.security.aes(options.aescfg);
	}
	return nodex;
}

nodex.extend = function(target, source) {
	for (var p in source) {
		if (source.hasOwnProperty(p)) {
			target[p] = source[p];
		}
	}
	return target;
}

module.exports = nodex;